import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { RecipeContext } from '../context/RecipeContext';  // Import RecipeContext

const Home = () => {
  const { recipes } = useContext(RecipeContext);  // Get the list of recipes from context

  console.log('Recipes:', recipes);   // Debug log to check if recipes are being retrieved from context

  return (
    <div>
      <h1>All Submitted Recipes</h1>
      {recipes.length > 0 ? (
        <ul>
          {recipes.map((recipe) => (
            <li key={recipe.id}>
              <Link to={`/recipe/${recipe.id}`}>{recipe.dishName}</Link>  {/* Link to recipe details */}
            </li>
          ))}
        </ul>
      ) : (
        <p>No recipes submitted yet.</p>
      )}
    </div>
  );
};

export default Home;
